import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Cart = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [data, setData] = useState([]);
  useEffect(() => {
    axios.get('https://fakestoreapi.com/products')
      .then(response => response.data)
      .then(data => {
        setData(data)
        setIsLoading(false)
      })
      .catch(error => console.log(error))
  }, []);

  if (isLoading) {
    return <p>Loading...</p>
  }

  return (
    <>
      <div className='wrapper'>
        <div className='container'>
          <div className='row mb-4'>
            {data.map(user => (
              <div className="col-xl-4 col-md-6 col-12 mb-4" key={user.id}>
              <div className="what-we-do-card">
                <div className="what-we-do-featured-icon">
                  <img alt={user.category} className="img-fluid" src={user.image} />
                </div>
                <div className="what-we-do-detail">
                  <h2 class="section-heading">{user.title}</h2>
                  <p>{user.description}</p>
                </div>
              </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
}

export default Cart;
