import React, { useState, useEffect } from 'react';
import axios from 'axios';


const styles = {
  section: {
    fontFamily: "-apple-system",
    fontSize: "1rem",
    fontWeight: 1.5,
    lineHeight: 1.5,
    color: "#292b2c",
    backgroundColor: "#fff",
    padding: "0 2em"
  },
  wrapper: {
    textAlign: "center",
    maxWidth: "950px",
    margin: "0 auto",
    border: "1px solid #e6e6e6",
    padding: "40px 25px",
    marginTop: "50px"
  },
  avatar: {
    margin: "-90px auto 30px",
    width: "100px",
    borderRadius: "50%",
    objectFit: "cover",
    marginBottom: "0"
  },
  quote: {
    lineHeight: 1.5,
    fontWeight: 300,
    marginBottom: "25px",
    fontSize: "1.375rem"
  },
  name: {
    marginBottom: "0",
    fontWeight: 600,
    fontSize: "1rem"
  },
  position: { fontWeight: 400 }
};

const Cart = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [data, setData] = useState([]);
  useEffect(() => {
    axios.get('https://fakestoreapi.com/products')
      .then(response => response.data)
      .then(data => {
        setData(data)
        setIsLoading(false)
      })
      .catch(error => console.log(error))
  }, []);

  if (isLoading) {
    return <p>Loading...</p>
  }

  return (
    <>
      <div className='wrapper'>
        <div className='container'>
          <div className='row mb-4'>
            {data.map(user => (
              <div className="col-xl-4 col-md-6 col-12 mb-4" key={user.id}>
              <div className="what-we-do-card" style={{}}>
                <div className="what-we-do-featured-icon">
                  <img alt={user.category} className="img-fluid" src={user.image} />
                </div>
                <div className="what-we-do-detail">
                  <h2 class="section-heading">{user.title}</h2>
                  <p>{user.description}</p>
                </div>
              </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
}

export default Cart;
